#include<ulib.h>
int main(u64 arg1, u64 arg2, u64 arg3, u64 arg4, u64 arg5) {
    // Array to store read and write fds of the persistent pipe.
	int fd[2];
	// Create the persistent pipe.
	int ret_code = ppipe (fd);
	if (ret_code < 0) {
		printf ("Pipe op is failed!!!\n");
		return -1;
	}
    // Expected return will be 0.
    printf ("%d\n", ret_code);

    //write 2000 bytes
    ret_code=write(fd[1],"",2000);
    if (ret_code < 0) {
        printf("Write to persistent pipe failed!!!\n");
        return -1;
    }
	printf ("%d\n", ret_code);

    //close the write end
    printf ("%d\n", close (fd[1]));

    //write 2000 bytes
    ret_code=write(fd[1],"",2000);
    if (ret_code < 0) {
        printf("Write to persistent pipe failed!!!\n");
        return -1;
    }
	printf ("%d\n", ret_code);

	return 0;

}
